var requirejs,require,define;
;(function(){

	var map={};

	var rq=function(list,func){
	
		if((typeof list=='number'||typeof list=='string')&&arguments.length==1){
			return map[list];
		}
	
	
		if(list instanceof Array){
			list=getList(list);
		}else{
			func=list;
			list=[];
		}
		
		if(typeof func=='function'){
			func.apply(this,list);
		}
	};

	var getList=function(list){
		var ret=[];
		for( var i=0; i<list.length;i++){
			ret.push(map[list[i]]||null);
		}
		return ret;
	};

	var def=function(list,func,pre){
		if(list instanceof Array){
			list=getList(list);
		}else{
			pre=func;
			func=list;
			list=[];
		}
		
		if(typeof func=='function'){
			map[pre]=func.apply(this,list);
		}else{
			map[pre]=func;
		}
	};
	
	def.amd={jQuery:true};
	
	require=rq;
	requirejs=rq;
	define=def;
})(this);